mapper.select:
select s.*, c.name clazzName
from student s left join clazz c on s.clazz_id = c.id


        s.name like concat('%',#{name},'%')


        and s.year_level = #{yearLevel}


        and s.clazz_id = #{clazzId}


order by s.update_time desc

mapper.update:
update student
name = #{name}, no = #{no}, gender = #{gender}, phone = #{phone}, id_card = #{idCard}, enrollment_status = #{enrollmentStatus}, address = #{address}, year_level = #{yearLevel}, graduation_date = #{graduationDate}, clazz_id = #{clazzId}, gpa = #{gpa}, credits = #{credits}, update_time = #{updateTime},
where id = #{id}
